        document.addEventListener('DOMContentLoaded', function() {
            // Aggiungi interattività dolce
            const methods = document.querySelectorAll('.method');
            const ageCards = document.querySelectorAll('.age-card');

            [...methods, ...ageCards].forEach(element => {
                element.addEventListener('mouseenter', function() {
                    this.style.transform = 'translateY(-3px)';
                    this.style.transition = 'transform 0.3s ease';
                    this.style.boxShadow = '0 12px 35px rgba(0, 0, 0, 0.15)';
                });

                element.addEventListener('mouseleave', function() {
                    this.style.transform = 'translateY(0)';
                    this.style.boxShadow = '';
                });
            });

            // Animazione gentile per i cuori fluttuanti
            const hearts = document.querySelectorAll('.floating-heart');
            hearts.forEach((heart, index) => {
                heart.style.animationDelay = `${index * 1.3}s`;
            });
        });
    </script>
