    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Animazioni per le card al passaggio del mouse
            const sections = document.querySelectorAll('.section, .method, .problem-solution');
            sections.forEach(section => {
                section.addEventListener('mouseenter', function() {
                    this.style.transform = 'translateY(-3px)';
                    this.style.boxShadow = '0 12px 35px rgba(0, 0, 0, 0.15)';
                    this.style.transition = 'all 0.3s ease';
                });
                section.addEventListener('mouseleave', function() {
                    this.style.transform = 'translateY(0)';
                    this.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.1)';
                });
            });

            // Animazione per gli elementi fluttuanti
            const floatingElements = document.querySelectorAll('.floating-element');
            floatingElements.forEach((element, index) => {
                element.style.animationDelay = `${index * 2}s`;
            });

            // Effetto di apparizione graduale per i contenuti
            const observer = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        entry.target.style.opacity = '1';
                        entry.target.style.transform = 'translateY(0)';
                    }
                });
            }, { threshold: 0.1 });

            // Applica l'observer a tutte le sezioni
            document.querySelectorAll('.section, .wisdom-box, .magic-moment').forEach(el => {
